import { Command, program } from "commander";
import act from "./act";
import { writeFileSync } from "fs";

export default () => {
  return new Command("gen-ts")
    .description(
      "generate a TypeScript file that provides the corresponding type definitions for the JavaScript file generated by the gen-js command",
    )
    .option(
      "--output-file-path <outputFilePath>",
      "specify the output file to be written instead of being piped to stdout",
    )
    .action(async ({ outputFilePath }) => {
      const { globalVariableName, envSchemaFilePath } = program.opts();

      const { output } = await act({
        envSchemaFilePath,
        globalVariableName,
      });

      if (outputFilePath) {
        writeFileSync(outputFilePath, output, "utf8");
      } else {
        console.log(output);
      }
    });
};
